import React, { useState } from "react";

import TileCard from "./TileCard";

function TileArea(props) {
  const [addCard, newAddCard] = useState(
    <div>
      <TileCard />
    </div>
  );

  function funAddCard() {
    newAddCard(
      <div>
        <TileCard />
      </div>
    );
  }

  return (
    <div>
      <button className="button" onClick={funAddCard}>
        CLICK
      </button>
      {addCard}
      <TileCard
        name={props.item[0].name}
        job={props.item[0].job}
        hobby={props.item[0].hobby}
      />
      <TileCard
        name={props.item[1].name}
        job={props.item[1].job}
        hobby={props.item[1].hobby}
      />
    </div>
  );
}

export default TileArea;
