import React, { useState, Fragment } from "react";
import TileArea from "./components/TileArea";
import "./components/TileArea.css";

const dataObject = [
  { name: "Robert", job: "Walmart", hobby: "Finding rocks" },
  { name: "Tom", job: "Smith's", hobby: "Swimming" },
  { name: "Kyle", job: "Chevron", hobby: "Hanging with friends" },
  { name: "Ellie", job: "Joe's Crabshack", hobby: "Running" },
  { name: "Kelly", job: "Winco", hobby: "Gaming" },
];

const arrayNum = [
  9, 3, 4, 5, 3, 4, 5, 3, 2, 3, 5, 5, 6, 4, 7, 8, 4, 5, 3, 7, 3, 5,
];

function runArray() {
  dataObject.forEach((element, index) => {
    console.log(element, index);
  });
  // ----------------
  const triple = arrayNum.map((item) => {
    return item * 2;
  });
  console.log(triple);
  // -----------------
  const odds = arrayNum.filter((item) => {
    return item % 2 === 1;
  });
  console.log(odds);
  // -----------------
}

function App() {
  return (
    <div className="tile">
      <button className="button" onClick={runArray}>
        CLICK
      </button>
      <div className="tile">
        <TileArea
          name={dataObject[0].name}
          job={dataObject[0].job}
          hobby={dataObject[0].hobby}
        />
        <TileArea
          name={dataObject[1].name}
          job={dataObject[1].job}
          hobby={dataObject[1].hobby}
        />
        <TileArea
          name={dataObject[2].name}
          job={dataObject[2].job}
          hobby={dataObject[2].hobby}
        />
        <TileArea
          name={dataObject[3].name}
          job={dataObject[3].job}
          hobby={dataObject[3].hobby}
        />
        <TileArea
          name={dataObject[3].name}
          job={dataObject[3].job}
          hobby={dataObject[3].hobby}
        />
        <TileArea
          name={dataObject[3].name}
          job={dataObject[3].job}
          hobby={dataObject[3].hobby}
        />
      </div>
    </div>
  );
}

export default App;
