  const [addCard, newAddCard] = useState("");

  function addCardHandler() {
    newAddCard("This passes from Button to TitleArea");
    console.log("button.js produced me");
    refresher(addCard);
  }

  function refresher(refresh) {
    props.getDiv(refresh);
  }

  return (
    <button className="button" onClick={addCardHandler}>
      CLICK
    </button>
  );
}

export default Button;
