import React, { useState } from "react";

import "./Button.css";

function Button(props) {
  const [name, setName] = useState("");
  const [job, setJob] = useState("");
  const [hobby, setHobby] = useState("");

  function nameHandler(event) {
    setName(event.target.value);
  }

  function jobHandler(event) {
    setJob(event.target.value);
  }

  function hobbyHandler(event) {
    setHobby(event.target.value);
  }

  function addCardHandler(event) {
    // stops page from resfreshing thing... cant remember
    event.preventDefault();

    const buttonArray = [{ name: name, job: job, hobby: hobby }];

    console.log("button.js");
    props.getDiv(buttonArray[0]);
  }

  return (
    <div>
      {/* <button className="button" onClick={addCardHandler}>
        CLICK
      </button> */}
      <form onSubmit={addCardHandler}>
        <input type="text" onChange={nameHandler}>
          {name}
        </input>
        <input type="text" onChange={jobHandler}>
          {job}
        </input>
        <input type="text" onChange={hobbyHandler}>
          {hobby}
        </input>
        <button className="button" type="submit">
          SUBMIT
        </button>
      </form>
    </div>
  );
}

export default Button;
